record.field:
action = record.payfast_test_signature()
if action['result']:
    message = f"SUCCESS: {action['status']}
Test data: {action['data']}
Signature: {action['signature']}"
else:
    message = f"ERROR: {action['status']}
Test data: {action['data']}
Signature: {action['signature']}"
return {
    'type': 'ir.actions.act_window',
    'name': 'Signature Test Result',
    'view_mode': 'form',
    'res_model': 'payment.acquirer',
    'res_id': record.id,
    'target': 'new',
    'context': {'default_message': message}
}
